import { Pressable } from "react-native";
import { StyleSheet, Text, View, Image, Linking } from "react-native";
import devloper from "../assets/images/devloper.jpg";
import Ionicons from "@expo/vector-icons/Ionicons";

function About({ navigation }) {
  return (
    <View style={styles.container}>
      <Text style={styles.heading}>App Developed By:</Text>
      <Image source={devloper} style={styles.image} />
      <Text style={styles.name}>Arunabh Jain</Text>
      <View style={styles.social}>
        <Text
          style={styles.links}
          onPress={() =>
            Linking.openURL("https://www.linkedin.com/in/arunabh-jain/")
          }
        >
          <Ionicons name="logo-linkedin" size={32} color="#14bf98" />
        </Text>
        <Text
          style={styles.links}
          onPress={() =>
            Linking.openURL("https://www.instagram.com/arunabh_jain/")
          }
        >
          <Ionicons name="logo-instagram" size={32} color="#14bf98" />
        </Text>
        <Text
          style={styles.links}
          onPress={() =>
            Linking.openURL("https://www.facebook.com/jain.arunabh/")
          }
        >
          <Ionicons name="logo-facebook" size={32} color="#14bf98" />
        </Text>
        <Text
          style={styles.links}
          onPress={() => Linking.openURL("https://wa.me/+918384922006")}
        >
          <Ionicons name="logo-whatsapp" size={32} color="#14bf98" />
        </Text>
      </View>
      <Text style={styles.heading}>Tech Stack</Text>
      <View style={styles.teContainer}>
        <View style={styles.detailsContainer}>
          <Text style={styles.details}>Frontend: React Native</Text>
          <Text style={styles.details}>Backend: FastAPI</Text>
          <Text style={styles.details}>API Hosting: Heroku</Text>
          <Text style={styles.details}>ML Model: Random Forest Classifier</Text>
        </View>
        <View style={styles.detailsContainer}>
          <Text style={styles.details}>Frontend: React Native</Text>
          <Text style={styles.details}>Backend: FastAPI</Text>
          <Text style={styles.details}>API Hosting: Heroku</Text>
          <Text style={styles.details}>ML Model: Random Forest Classifier</Text>
        </View>
      </View>

      {/* <Pressable
        style={styles.button}
        onPress={() => navigation.navigate("PREDICT")}
      >
        <Text style={styles.buttonText}>PREDICT APPROVAL</Text>
      </Pressable> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#382B73",
    alignItems: "center",
    justifyContent: "center",
    padding: 20,
    color: "#fdfdfd",
  },
  heading: {
    margin: 10,
    fontSize: 20,
    padding: 5,
    fontWeight: "bold",
    color: "#fdfdfd",
  },
  name: {
    fontSize: 32,
    width: "100%",
    textAlign: "center",
    padding: 5,
    fontWeight: "bold",
    color: "#F7CA15",
    marginBottom: 10,
  },
  details: {
    fontSize: 20,
    padding: 5,
    fontWeight: "bold",
    color: "#fdfdfd",
    textAlign: "left",
    width: "100%",
  },
  button: {
    height: 50,
    borderRadius: 30,
    backgroundColor: "#14bf98",
    marginTop: 50,
    width: 300,
    display: "flex",
    justifyContent: "center",
    alignContent: "center",
    alignItems: "center",
  },
  buttonText: {
    color: "#382B73",
    fontSize: 20,
    fontWeight: "bold",
  },
  image: {
    width: 150,
    height: 150,
    borderRadius: "100%",
    borderColor: "#14bf98",
    borderWidth: 5,
    margin: 10,
  },
  social: {
    display: "flex",
    width: 200,
    flexDirection: "row",
    justifyContent: "space-between",
    alignContent: "center",
    alignItems: "center",
  },
});

export default About;
