import { SocialIcon } from "@rneui/themed";
import { Pressable } from "react-native";
import { StyleSheet, Text, View, ImageBackground } from "react-native";

function About({ navigation }) {
  return (
    <View style={styles.container}>
      <Text style={styles.heading}>App Developed By:</Text>
      <ImageBackground
        src={require("../assets/images/developer.jpg")}
        style={styles.headerImage}
      >
        <View style={styles.image} />
      </ImageBackground>
      <Text style={styles.name}>Arunabh Jain </Text>

      <Text style={styles.heading}>Tech Stack</Text>
      <Text style={styles.details}>Frontend: Expo (ReactNative)</Text>
      <Text style={styles.details}>Backend: FastAPI</Text>
      <Text style={styles.details}>Backend Hosting: Heroku</Text>
      <Text style={styles.details}>ML Model: Random Forest Classifier</Text>
      <Text style={styles.details}>
        So what are you waiting for, predict your chances of loan approval using
        our app.
      </Text>
      <Pressable
        style={styles.button}
        onPress={() => navigation.navigate("PREDICT")}
      >
        <Text style={styles.buttonText}>PREDICT APPROVAL</Text>
      </Pressable>
      <Text style={styles.heading}>App Developed By:</Text>
      <Text style={styles.name}>Arunabh Jain </Text>
      <View>
        <SocialIcon type="instagram" />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#382B73",
    alignItems: "center",
    justifyContent: "center",
    padding: 20,
    color: "#fdfdfd",
  },
  heading: {
    marginTop: 50,
    fontSize: 20,
    padding: 5,
    fontWeight: "bold",
    color: "#fdfdfd",
  },
  name: {
    fontSize: 20,
    padding: 5,
    fontWeight: "bold",
    color: "#fdfdfd",
  },
  details: {
    fontSize: 20,
    padding: 5,
    fontWeight: "bold",
    color: "#fdfdfd",
    textAlign: "left",
    width: "100%",
  },
  button: {
    height: 50,
    borderRadius: 30,
    backgroundColor: "#14bf98",
    marginTop: 50,
    width: 300,
    display: "flex",
    justifyContent: "center",
    alignContent: "center",
    alignItems: "center",
  },
  buttonText: {
    color: "#382B73",
    fontSize: 20,
    fontWeight: "bold",
  },
  image: {
    width: 100,
    height: 100,
  },
  headerImage: {
    width: "100%",
    height: "100%",
  },
});

export default About;
